mapper.select:
SELECT
MAX(guidegprice) `max`,MIN(guidegprice) `min` FROM db_model WHERE
gid=#{gid} GROUP BY gid;

		SELECT gid,gname,gfirstimage,title,maxprice,minprice FROM db_goods
